import React from 'react';
import './ShortcutsModal.css'; // Crea un archivo de estilos para este modal

const ShortcutsModal = ({ isOpen, onClose }) => {
  if (!isOpen) return null;

  return (
    <div className="shortcuts-modal-container" onClick={onClose}>
      <div className="shortcuts-modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="shortcuts-modal-header">
          <h3>Atajos</h3>
        </div>
        <div className="shortcuts-list">
          <div className="shortcut-item">
            <span className="shortcut-key">Ctrl + S</span> - Guardar
          </div>
          <div className="shortcut-item">
            <span className="shortcut-key">Ctrl + C</span> - Copiar
          </div>
          <div className="shortcut-item">
            <span className="shortcut-key">Ctrl + V</span> - Pegar
          </div>
          {/* Agrega más atajos según sea necesario */}
        </div>
      </div>
    </div>
  );
};

export default ShortcutsModal;
