import React, { useRef, useEffect } from 'react';
import './logout.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faCog, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';

const ModalProfile = ({ isOpen, onClose, anchorRef }) => {
  const modalRef = useRef(null);

  useEffect(() => {
    if (isOpen && modalRef.current && anchorRef.current) {
      const rect = anchorRef.current.getBoundingClientRect();
      modalRef.current.style.top = `${rect.bottom + 5}px`;  // Justo debajo del botón
      modalRef.current.style.left = `${rect.left - rect.width / 2}px`;
    }
  }, [isOpen, anchorRef]);

  if (!isOpen) return null;

  return (
    <div className="modal-container" ref={modalRef} onClick={(e) => e.stopPropagation()}>
      <div className="modal-header">
        <img src="https://via.placeholder.com/80" alt="Profile" className="profile-image" />
        <h2 className="profile-name">Ella Lindsey</h2>
        <p className="profile-role">Admin</p>
      </div>
      <div className="modal-buttons">
        <button className="modal-button profile-button">
          <FontAwesomeIcon icon={faUser} /> Profile
        </button>
        <button className="modal-button settings-button">
          <FontAwesomeIcon icon={faCog} /> Settings
        </button>
        <button className="modal-button logout-button">
          <FontAwesomeIcon icon={faSignOutAlt} /> Logout
        </button>
      </div>
    </div>
  );
};

export default ModalProfile;
