import React, { useState } from 'react';
import { Carousel, Products, Searcher } from "../../components";
import slides from "../../data/sliderData";
import productsData from '../../data/productsData';
import './Home.css';

const Home = ({ setItemCount, setTotalPrice }) => {
    const [products] = useState(productsData);
    const [filteredProducts, setFilteredProducts] = useState(productsData);
    const handleSearch = (searchTerm) => {
        const filtered = products.filter(product => 
            product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            product.description.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setFilteredProducts(filtered);
    };

    return (
        <div>

            <div className="home__products-header">
                <h1 className="home__products-title">Clientes Registrados</h1>
            </div>
            <div className="home_table-container">
            <table className="customer__table">
                    <thead className="customer_thead">
                        <tr className="customer__tr">
                            <th className="customer__th">Productos</th>
                            <th className="customer_th">Imagen</th>
                            <th className="customer__th">Price</th>
                            <th className="customer_th">Quantity</th>
                            <th className="customer_th">Total</th>
                            <th className="customer_th">Action</th>
                        </tr>
                    </thead>
                    <tbody className="cart-shop__tbody">

                    </tbody>
                </table>

            </div>
            <Searcher onSearch={handleSearch} />

        </div>
    );
};

export default Home;
