import React, { useState, useEffect } from 'react';
import './Contract.css';
import Header from '../../components/Header';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import Table from '../../components/Table';
import Section from '../../components/Section';
import apiClient from "../../axios";// Asegúrate de tener configurado tu cliente API

const Contract = ({ handleLogout }) => {
  const [data, setData] = useState([]);

  // Hook useEffect para consumir el servicio al montar el componente
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await apiClient.get("/contracts/1407230823");
        if (Array.isArray(response.data)) {
          setData(response.data); // Si es un array, lo dejamos tal cual
      } else {
          setData([response.data]); // Si es un objeto, lo encapsulamos en un array
      }
      } catch (error) {
        console.error("Error al obtener los datos del servicio", error);
      }
    };

    fetchData();
  }, []); // El array vacío [] asegura que se ejecute solo una vez al montar el componente

  const columns = [
    { title: "Número de contrato", key: "numCont" },
    { title: "Cliente", key: "razonSocial" },
    { title: "Cédula", key: "cif" },
  ];

  return (
    <div className="home-container">
      {/* Renderiza el Header */}
      <Header onLogout={handleLogout} title='Contratos' />

      {/* Aquí va el resto del contenido de tu Home */}
      <div className="home-content">
        <Section>
           <div className="filter-form">
              <div className="form-group">
                <label htmlFor="license">Contrato</label>
                <input
                  type="text"
                  id="license"
                  placeholder="Contrato"
                />
              </div>
              <div className="form-group">
                <label htmlFor="customer">Cliente</label>
                <input
                  type="text"
                  id="customer"
                  placeholder="Cliente"
                />
              </div>
              <button className="search-button">
                <FontAwesomeIcon icon={faSearch} className="search-icon" />
                Buscar
              </button>
           </div>
         </Section>
        <Table isEdit={false} isDelete={true} isCompany={true} title='Lista de Usuarios' rows={data} columns={columns} />
      </div>
    </div>
  );
};

export default Contract;
